#!/usr/bin/env perl 
#===============================================================================
#
#         FILE: day1a.pl
#
#        USAGE: ./day1a.pl  
#
#  DESCRIPTION: Day 1 - Santa's elevator, final destination and first time in
#               the basement
#
#      OPTIONS: ---
# REQUIREMENTS: ---
#         BUGS: ---
#        NOTES: ---
#       AUTHOR: Mark T. Johnson (mtj), markj@matzsoft.com
# ORGANIZATION: MATZ Software & Consulting
#      VERSION: 1.0
#      CREATED: 12/08/15 10:43:28
#     REVISION: ---
#===============================================================================

use strict;
use warnings;
use utf8;
use 5.010;

my $floor = 0;
my $position = 0;
my $data = do { local $/; <DATA> };

$data =~ s/\n//g;

say ( ( $data =~ tr/(/(/ ) - ( $data =~ tr/)/)/ ) );

for my $position ( 0 .. length( $data ) - 1 ) {
    my $direction = substr $data, $position, 1;

    if ( $direction eq '(' ) {
        $floor++;
    } elsif ( $direction eq ')' ) {
        $floor--;
        if ( $floor == -1 ) {
            say $position + 1;
            exit( 0 );
        }
    } else {
        die "Invalid input $position at position ", $position + 1, "\n";
    }
}

say "Never makes it to the basement";

__END__
